/*==================================================
StudentView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display the single student view page.
================================================== */
import React from "react";
import { Link } from "react-router-dom";
import Button from '@mui/material/Button';

const StudentView = (props) => {
  const { student, onDeleteStudent } = props;

  // Render a single Student view 
  return (
    <div>
      <h1>{student.firstname + " " + student.lastname}</h1>
      <h3>{student.campus ? student.campus.name : "Not enrolled in any campus"}</h3>
      <Link to={`/editstudent/${student.id}`}>
        <Button variant="contained" color="primary">Edit Student</Button>
      </Link>
      <Button variant="contained" color="error" onClick={() => onDeleteStudent(student.id)} style={{ marginLeft: '10px' }}>Delete Student</Button>
    </div>
  );
};

export default StudentView;
