/*==================================================
CampusView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display a single campus and its students (if any).
================================================== */
import React from "react";
import { Link } from "react-router-dom";

// Take in props data to construct the component
const CampusView = (props) => {
  const { campus, onAddStudent, onDeleteStudent, onEditCampus, onDeleteCampus } = props;
  
  // Render a single Campus view with list of its students
  return (
    <div>
      <h1>{campus.name}</h1>
      <p>{campus.address}</p>
      <p>{campus.description}</p>
      {campus.students.length === 0 ? (
        <p>No students are enrolled at this campus.</p>
      ) : (
        campus.students.map(student => {
          let name = student.firstname + " " + student.lastname;
          return (
            <div key={student.id}>
              <Link to={`/student/${student.id}`}>
                <h2>{name}</h2>
              </Link>
              <button onClick={() => onDeleteStudent(student.id)}>Delete Student</button>
            </div>
          );
        })
      )}
      <button onClick={onAddStudent}>Add Student</button>
      <button onClick={onEditCampus}>Edit Campus</button>
      <button onClick={onDeleteCampus}>Delete Campus</button>
    </div>
  );
};

export default CampusView;
